mapper.update:
update chat_user
set nick = #{nick}
where uid = #{uid}

update chat_user
set password = #{password}
where uid = #{uid}

mapper.select:
select count(*)
from (
     select *
     from chat_relation
     where uid_auser = #{uid} and uid_buser = #{uid1}
     union
     select *
     from chat_relation
     where uid_auser = #{uid1} and uid_buser = #{uid}
) as tmp

select uid
from chat_user
where username = #{fromUsername}
